<script>
    $("#send-details0").ready(
        function () {
            function autocom(id, tid, idout, family, tinterval) {

                var idout = idout;
                var typingTimer;
                var sttime;
                var doneTypingInterval = tinterval;
                var $input = $(id);
                $input.on('keyup', function () {
                    clearTimeout(typingTimer);
                    typingTimer = setTimeout(doneTyping, doneTypingInterval);
                });
                $input.on('keydown', function () {
                    clearTimeout(typingTimer);
                });

                function doneTyping() {
                    var query = $(id).val();
                    if (query !== '') {
                        var _token = $('input[name="_token"]').val();
                        var _t = tid;
                        var _family = family;
                        $.ajax({
                            url: "https://shajaresaz.com/addmember/fetch",
                            method: "POST",
                            data: {query: query, _token: _token, tid: _t, family: _family},
                            success: function (data) {
                                if (data !== '') {
                                    $(idout).fadeIn();
                                    $(idout).html(data);
                                    $(id).focusout(function () {
                                        clearTimeout(sttime);
                                        sttime = setTimeout(function () {
                                            $(idout).fadeOut()
                                        }, 10000);
                                    });
                                    $(id).focusin(function () {
                                        clearTimeout(sttime);
                                    });
                                    //$(id).attr('autocomplete', 'disabled');
                                } else {
                                    //$(id).attr('autocomplete', 'on');
                                }
                            }
                        });
                    } else {
                        $(idout).fadeOut()
                    }
                }

                $(id).on('focusout', function () {
                    $(idout).fadeOut();
                });
                $(id).on('onblur', function () {
                    $(idout).fadeOut();
                });
                $(idout).on('click', 'li', function () {
                    $(id).val($(this).text());
                    $(idout).fadeOut();
                });
            }

            autocom('#firstname', 1, '#firstnameList', '359512791', 250);
            autocom('#lastname', 2, '#lastnameList', '359512791', 250);
            autocom('#birthplace', 3, '#birthplaceList', '359512791', 350);
            autocom('#deathplace', 4, '#deathplaceList', '359512791', 350);
            autocom('#city', 5, '#cityList', '359512791', 350);
        });
</script>
