    function addremovetocookie(v, add = true) {
        c = getCookie("toggles_359512791") ?? '';
        cv = c.split("|");
        if (add) {
            cv.push(v.replace('#', ''));
        } else {
            var dindex = cv.indexOf(v.replace('#', ''));
            if (dindex !== -1) {
                cv.splice(dindex, 1);
            }
        }
        cv = cv.filter(onlyUnique);
        cv = cv.join('|');
        document.cookie = "toggles_359512791=" + cv + '; path=home/359512791';
