<script>
    var isIgnore = false;

    function scrollToElement(id, color = 'goldenrod', shadowwidth = 30, shadowcolor = 'black', timerint = 1800,
        backgroundColor = 'crimson') {
        if (isIgnore)
            return;
        isIgnore = true;
        var v2 = 'item_' + id;
        var elements = document.querySelectorAll('#' + v2);
        var velement = null;
        for (var i = 0; i < elements.length; i++) {
            // offsetParent is null if the element or any of its parents has display:none
            if (elements[i].offsetParent !== null) {
                velement = elements[i];
                break;
            }
        }
        // fallback: if no visible element is found, use the first one (if exists)
        if (!velement && elements.length > 0) {
            velement = elements[0];
        }
        if (!velement) {
            isIgnore = false;
            ScrollToCentre();
            return false;
        }

        // expand the tree if the node is collapsed
        $('#' + v2).parents('ul').each(function () {
            eid = $(this).attr('id');
            if (eid === undefined)
                return false;
            $('#' + eid).show();
            let eidplus = $('#' + eid.replace('toggleli_', 'togglespan_'));
            if (eidplus.hasClass('togglecollapsed')) {
                eidplus.removeClass('togglecollapsed');
            }
        });
        velement.scrollIntoView({
            behavior: 'smooth',
            block: 'center',
            inline: 'center'
        });
        if (v2 === undefined)
            return false;


        // Store original styles to restore later
        let originalStyles = {
            borderTop: velement.style.borderTop,
            borderRight: velement.style.borderRight,
            borderBottom: velement.style.borderBottom,
            borderLeft: velement.style.borderLeft,
            transform: velement.style.transform,
            zIndex: velement.style.zIndex,
            borderImage: velement.style.borderImage,
            boxShadow: velement.style.boxShadow,
            backgroundColor: velement.style.backgroundColor
        };

        // Highlight the element
        velement.style.border = '5px solid ' + color;
        velement.style.boxShadow = 'inset 1500px 1500px 1500px ' + backgroundColor + ', 0 0 ' +
            shadowwidth + 'px ' + shadowcolor;
        velement.style.transform = 'scale(1.15)';
        velement.style.zIndex = 999 + Date.now();
        velement.style.backgroundColor = backgroundColor;

        // Revert back to the original styles after the specified timer interval
        setTimeout(function () {
            velement.style.borderTop = originalStyles.borderTop;
            velement.style.borderRight = originalStyles.borderRight;
            velement.style.borderBottom = originalStyles.borderBottom;
            velement.style.borderLeft = originalStyles.borderLeft;
            velement.style.transform = originalStyles.transform;
            velement.style.zIndex = originalStyles.zIndex;
            velement.style.boxShadow = originalStyles.boxShadow;
            velement.style.backgroundColor = originalStyles.backgroundColor;
            velement.style.borderImage = originalStyles.borderImage;
            isIgnore = false;
        }, timerint);
    }
</script>

