<script>
    (function (win, doc) {
        'use strict';
        if (!doc.querySelectorAll || !win.addEventListener) {
            // doesn't cut the mustard.
            return;
        }
        var forms = doc.querySelectorAll('form[method="post"]'),
            formcount = forms.length,
            i,
            submitting = false,
            checkForm = function (ev) {
                if (submitting) {
                    ev.preventDefault();
                } else {
                    submitting = true;
                    var ge = doc.createElement('progress');
                    ge.setAttribute("id", "submitprogressbar");
                    ge.style.cssText = 'display:block';
                    this.appendChild(ge);
                }
            };
        for (i = 0; i < formcount; i = i + 1) {
            forms[i].addEventListener('submit', checkForm, false);
        }
    }(this, this.document));
</script>
